formulas.expression:
AND(
OR(
ISCHANGED({!$Record.StageName}),
{!booleanIsNew}
),
ISPICKVAL({!$Record.Type}, "New"),
TEXT({!$Record.StageName}) = "Closed Won",

/* uses Most Recent Cancellation date from KStore if PWR custom setting is active */
IF({!$Setup.PWR_Phase_1_Custom_Setting__c.Is_Active__c},
AND(
MONTH({!$Record.Klaviyo_Product_Usage__r.Most_Recent_Cancellation_Date__c}) = MONTH({!$Record.CloseDate}),
YEAR({!$Record.Klaviyo_Product_Usage__r.Most_Recent_Cancellation_Date__c}) = YEAR({!$Record.CloseDate})
),

/* otherwise uses date from Account */
AND(
MONTH({!$Record.Account.Cancellation_Date__c}) = MONTH({!$Record.CloseDate}),
YEAR({!$Record.Account.Cancellation_Date__c}) = YEAR({!$Record.CloseDate})
)
)

)

IF(
AND(
{!$Record.of_Churn_MRR__c} < 0,
TEXT({!$Record.StageName}) = "Closed Won",

/* Runs when stage is changed or when record is newly created */
OR(
ISCHANGED({!$Record.StageName}),
ISNEW()
)
),

/* Returns current date time if criteria is met, otherwise null */
{!$Flow.CurrentDateTime},
null
)
