ValidationRule.errorConditionFormula:
AND(

/* prevents rule from triggering for admins
NOT(CONTAINS($Profile.Name,&quot;System&quot;)),
NOT(CONTAINS($Profile.Name,&quot;Admin&quot;)),
$Profile.Name &lt;&gt;&quot;Operations&quot;,
*/


/* Rule is enforced only when a relevant field is changed */
OR(
ISCHANGED(of_Products__c),
ISCHANGED(Identify_Pain_I__c)
),

/* Opportunity lacks a product or identified pain */
OR(
NOT(of_Products__c > 0),
ISBLANK(Identify_Pain_I__c) 
)

)
